from selenium import webdriver
from pymongo import MongoClient
import time
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import Select, WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
from selenium.webdriver.common.keys import Keys
from selenium.webdriver.common.action_chains import ActionChains

driver = webdriver.Firefox()
#driver_content = webdriver.Firefox()
#driver = webdriver.Chrome()

my_email = 'study.ai_172@mail.ru'
my_password = 'NewPassword172'

driver.get('https://mail.ru/')
assert 'Mail.ru: –ø–æ—á—Ç–∞' in driver.title

driver.find_element_by_id('mailbox:login').send_keys(my_email)
button = driver.find_element_by_xpath("//div[contains(@class, 'mailbox__row mailbox__row_ i-clearfix')]")
time.sleep(1)
button.click()
time.sleep(1)
driver.find_element_by_id('mailbox:password').send_keys(my_password)
button = driver.find_element_by_xpath("//div[contains(@class, 'mailbox__row mailbox__row_ i-clearfix')]/label")
time.sleep(1)
button.click()
#time.sleep(30)
items = list()
len_items = 1
time.sleep(3)
WebDriverWait(driver, 40).until(EC.presence_of_element_located(
    (By.XPATH, "//div[contains(@class, 'scrollable g-scrollable scrollable_bright scrollable_footer scrollable_ponymode')]")
    )
)
scroll = driver.find_element_by_xpath("//div[contains(@class, 'scrollable g-scrollable scrollable_bright scrollable_footer scrollable_ponymode')]")
items = scroll.find_elements_by_xpath("//a[contains(@class, 'llct js-letter-list-item llct_pony-mode')]")
position = 0
new_link = ' '
last_link = '1'
mails = []
while new_link != last_link:
    items = scroll.find_elements_by_xpath("//a[contains(@class, 'llct js-letter-list-item llct_pony-mode')]")

    actions = ActionChains(driver)
    if position == 0:
        actions.click(items[position])
    else:
        actions.send_keys(Keys.ARROW_DOWN)

    actions.perform()
    time.sleep(1)

    mail = dict()
    WebDriverWait(driver, 30).until(EC.presence_of_element_located((By.XPATH, "//*[contains(@id, '_BODY')]")))
    last_link = new_link
    new_link = items[position].get_attribute('href')
    mail['author'] = items[position].find_element_by_class_name('ll-crpt').get_attribute('title')
    mail['theme'] = items[position].find_element_by_class_name('ll-sj__normal').text
    mail['date'] = items[position].find_element_by_class_name('llct__date').get_attribute('title')
    mail['text'] = driver.find_element_by_xpath("//*[contains(@id, '_BODY')]").text
    mails.append(mail)
    print(mail)
    position = position + 1 if position < len(items) - 1 else len(items) - 1

driver.quit()

client = MongoClient('localhost', 27017)
db = client['mail']['mailru']

db.insert_many(mails)

